const isValid=t=>{if(0===t.length)return!1;if(/^[*/]/.test(t))return!1;if(/[^\d\-+*/{[()]}.]/.test(t))return!1;if(t.match(/([+\-/*]){2}|\{[-+*/]?\}|\([-+*/]?\)|\[([-+*/])?\]|[([{]{1}[*/]{1}|[^\d]+\.|\.[^\d]+|\.$|^\.|[\d]+\.[\d]+\./))return!1;let e=[/\{/g,/\[/g,/\(/g],r=[/\}/g,/\]/g,/\)/g];for(let i=0;i<2;i++)if(e[i].test(t)){if(!r[i].test(t))return!1;if(t.match(e[i]).length!==t.match(r[i]).length)return!1}return!0};module.exports=isValid;