const toSciNotation=t=>{let o="string"==typeof t?parseFloat(t):t;if(isNaN(o)||!isFinite(o))return null;if(0===o)return"0e+0";const e=o<0?"-":"";o=Math.abs(o);const i=Math.floor(Math.log10(o));let r=o/Math.pow(10,i);r=parseFloat(r.toPrecision(15));let a=r.toString();a.includes(".")&&(a=a.replace(/\.?0+$/,""),a=a.replace(/\.$/,""));return`${e}${a}e${i>=0?"+":""}${i}`};module.exports=toSciNotation;